<?php
/*
        Template Name: new-pass
    */
get_header();
global $wpdb;
$wpdb->show_errors();
$key = $_GET['key'];
$email = $_GET['email'];
// var_dump($email);

if (isset($_POST['reset'])) {
    $newpass = $wpdb->escape($_POST['new_pass']);
    $againnewpass = $wpdb->escape($_POST['again_new_pass']);

    $error = array();
    if (empty($newpass) || empty($againnewpass)) {
        $error['restpass'] = 'New pass is empty!';
    }
    if (strpos($newpass, ' ') !== FALSE) {
        $error['restpass'] = 'Password can`t not use White Space!';
    }
    if ($newpass < 8) {
        $error['restpas'] = 'Password lenght must be upper 8 character!';
    }
    if (strpos($againnewpass, ' ') !== FALSE) {
        $error['restpass'] = 'Again password can`t not use White Space!';
    }
    if ($againnewpass < 8) {
        $error['restpas'] = 'Again new Password lenght must be upper 8 character!';
    }
    if (strcmp($newpass, $againnewpass) !== 0) {
        $error['password'] = "Password didn`t match!";
    }
    if (count($error) == 0) {
        $table_name = 'user-sign-up';
        $data = array(
            'password' => md5($newpass),
        );
        $where = array(
            'email' => $email,
        );

        $update = $wpdb->update($table_name, $data, $where);
        if ($update == 1) {
            $success = 'Password update Successfully!!';
            echo "<script>setTimeout(function(){ window.location.href = '" . home_url() . "/profile'; }, 300);</script>";
        } else {
            $error['error'] = "Something went wrong,Please try Again!";
        }
    }
}
